using System.Net.Http.Json;
using System.Text;
using System.Text.Json;
using AwesomeGithubPortfolio.Core.Interfaces;
using AwesomeGithubPortfolio.Core.Models;
using AwesomeGithubPortfolio.Core.Models.Responses;
using Microsoft.Extensions.Logging;
using Polly;
using Polly.Retry;

namespace AwesomeGithubPortfolio.Core.Services;

internal class GithubService : IGithubService
{
    private readonly HttpClient _client;
    private readonly AsyncRetryPolicy _policy;

    public GithubService(IHttpClientFactory clientFactory, ILogger<GithubService> logger)
    {
        _client = clientFactory.CreateClient("github");
        _policy = Policy
            .Handle<Exception>()
            .WaitAndRetryAsync(new[]
            {
                TimeSpan.FromSeconds(1),
                TimeSpan.FromSeconds(1),
                TimeSpan.FromSeconds(1)
            }, (exception, timeSpan) => { logger.Log(LogLevel.Error, $"Error trying to get data: {exception}"); });
    }

    public async Task<GitHubUser> FetchUserData(string username)
    {
        var user = await GetUser(username);
        if (user == null)
            return null;

        // if (Debugger.IsAttached)
        // {
        //     user.ContributionsThroughYears = JsonSerializer.Deserialize<List<ContributionsCollection>>(GitHubUserStats);
        //     return user;
        // }

        var tasks = new List<Task<ContributionsCollection>>();

        foreach (var year in user.ContributionsCollection.ContributionYears)
        {
            tasks.Add(FetchUserInformationByYear(username, year));
        }

        var result = await Task.WhenAll(tasks).ConfigureAwait(false);

        user.ContributionsThroughYears = result.Where(w => w != null).ToList();
        return user;
    }

    public async Task<bool> UserHasStarred(string username)
    {
        var request = new DefaultRequest()
        {
            Query = GithubOptions.UserStarredRepositories,
            OperationName = GithubOptions.OperationName,
            Variables = new { login = username }
        };
        var response = await _policy.ExecuteAndCaptureAsync(() => _client.PostAsJsonAsync("graphql", request, GithubOptions.DefaultJson));

        if (response.Outcome != OutcomeType.Successful || !response.Result.IsSuccessStatusCode) return false;

        var userInfo =
            await JsonSerializer.DeserializeAsync<DefaultResponse<UserData>>(
                await response.Result.Content.ReadAsStreamAsync(), GithubOptions.DefaultJson);

        return userInfo.Data.User.StarredRepositories.Nodes.Any(a => a.NameWithOwner.Equals("brunobritodev/awesome-github-portfolio"));
    }

    public async Task<string> FetchUserReadme(string githubUserLogin)
    {
        var path = $"repos/{githubUserLogin}/{githubUserLogin}/readme";

        var response = await _policy.ExecuteAndCaptureAsync(() => _client.GetFromJsonAsync<RepositoryFileContent>(path));
        _client.DefaultRequestHeaders.Clear();
        if (response.Outcome != OutcomeType.Successful) return string.Empty;

        var data = Convert.FromBase64String(response.Result.Content);
        return Encoding.UTF8.GetString(data);
    }

    public async Task<PortfolioViewModel> FetchCustomPortfolioFromFork(string username)
    {
        var path = $"repos/{username}/awesome-github-portfolio/contents/{username}.json";

        var response = await _client.GetAsync(path);

        if (!response.IsSuccessStatusCode)
            return null;
        var data = Convert.FromBase64String(await response.Content.ReadAsStringAsync());
        return JsonSerializer.Deserialize<PortfolioViewModel>(data, GithubOptions.DefaultJson);
    }

    public async Task<string> ChooseModel(string username)
    {
        var request = new DefaultRequest()
        {
            Query = GithubOptions.UserFollowers,
            OperationName = GithubOptions.OperationName,
            Variables = new { login = username }
        };
        var response = await _policy.ExecuteAndCaptureAsync(() => _client.PostAsJsonAsync("graphql", request, GithubOptions.DefaultJson));

        if (response.Outcome != OutcomeType.Successful || !response.Result.IsSuccessStatusCode) return "gpt-4o-mini";

        var userInfo =
            await JsonSerializer.DeserializeAsync<DefaultResponse<UserData>>(
                await response.Result.Content.ReadAsStreamAsync(), GithubOptions.DefaultJson);


        var followingMe = userInfo?.Data?.User.Following.Nodes.Any(a => a.Login.Equals("brunobritodev"));
        if (followingMe is not null && followingMe.Value)
            return "gpt-4o";
        
        return "gpt-4o-mini";
    }

    private async Task<GitHubUser> GetUser(string username)
    {
        var request = new DefaultRequest()
        {
            Query = GithubOptions.UserInformationQuery,
            OperationName = GithubOptions.OperationName,
            Variables = new { login = username }
        };
        var response = await _policy.ExecuteAndCaptureAsync(() => _client.PostAsJsonAsync("graphql", request, GithubOptions.DefaultJson));

        if (response.Outcome != OutcomeType.Successful || !response.Result.IsSuccessStatusCode) return null;

        var userInfo =
            await JsonSerializer.DeserializeAsync<DefaultResponse<UserData>>(
                await response.Result.Content.ReadAsStreamAsync(), GithubOptions.DefaultJson);


        return userInfo?.Data?.User;
    }

    private async Task<ContributionsCollection> FetchUserInformationByYear(string username, int year)
    {
        var request = new DefaultRequest()
        {
            Query = GithubOptions.ContributionsFromYear.Replace("{year}", $"{year}").Replace("{next_year}", (year + 1).ToString()),
            OperationName = GithubOptions.OperationName,
            Variables = new { login = username }
        };
        var response = await _policy.ExecuteAndCaptureAsync(() => _client.PostAsJsonAsync("graphql", request, GithubOptions.DefaultJson));

        if (response.Outcome != OutcomeType.Successful || !response.Result.IsSuccessStatusCode) return null;

        var userInfo = await JsonSerializer.DeserializeAsync<DefaultResponse<UserData>>(await response.Result.Content.ReadAsStreamAsync(), GithubOptions.DefaultJson);
        userInfo.Data.User.ContributionsCollection.Year = year;
        return userInfo?.Data.User.ContributionsCollection;
    }

